C#
C++/CLI
publicclassProgress
publicclassProgress
publicrefclassProgress
publicrefclassProgress
C#
Progress oProgress =newProgress("SimpleProgress");
oProgress.ShowImmediately();//part 1oProgress.BeginPart(25.0,"");
oProgress.SetActionText("part1");
oProgress.SetNeededSteps(1);
oProgress.Step(1);
System.Threading.Thread.Sleep(2500);// TODO: Some processingoProgress.EndPart(false);//part 2oProgress.BeginPart(30.0,"");
oProgress.SetNeededSteps(3);// call SetNeededSteps with the count of steps followingoProgress.SetActionText("part2 step1");
oProgress.Step(1);
System.Threading.Thread.Sleep(1000);// TODO: Some processingoProgress.SetActionText("part2 step2");
oProgress.Step(1);
System.Threading.Thread.Sleep(1000);// TODO: Some processingoProgress.SetActionText("part2 step3");
oProgress.Step(1);
System.Threading.Thread.Sleep(1000);// TODO: Some processingoProgress.EndPart(false);//part 3oProgress.BeginPart(45.0,"");// Here is a sum of 100% reached!oProgress.SetActionText("part3");
oProgress.SetNeededSteps(1);
oProgress.Step(1);
System.Threading.Thread.Sleep(4500);// TODO: Some processingoProgress.EndPart(true);// The window is destroyed with the call EndPart(true) and the progress is prepared to be destroyed.
C#
Progress progress =newProgress("SimpleProgress");
progress.SetAllowCancel(true);
progress.SetAskOnCancel(true);
progress.SetTitle("Replace parts");
progress.ShowImmediately();

DMObjectsFinder finder =newDMObjectsFinder(oProject);
ArticleReference[] articleReferences = finder.GetArticleReferences(null);intnIteration = 0;foreach(ArticleReference articleReferenceinarticleReferences)
{// Set new valuesprogress.BeginPart(100.0 / articleReferences.Length,"Iteration : "+ (nIteration++).ToString());
    articleReference.PartNr ="New part number";
    articleReference.VariantNr ="New variant";
    articleReference.StoreToObject();newDeviceService().UpdateDevice(articleReference.ParentObject);
    progress.EndPart();
}

progress.EndPart(true);
